import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import axios from "../axios";
import Loading from "./Loading";
import { Badge, Button, Table } from "react-bootstrap";

function OrdersAdminPage() {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(false);
  const products = useSelector((state) => state.products);
  const [orderToShow, setOrderToShow] = useState([]);
  const [show, setShow] = useState(false);

  function markShipped(orderId, ownerId) {
    axios.patch(`/orders/$/{orderId}/mark-shipped`, { ownerId }).then(({data}) => setOrders(data))
    .catch((e) => console.log(e));
  }
  function showOrder(productsObj) {
    const productsToShow = products.filter((product) => productsObj[product._id]);
    productsToShow = productsToShow.map((product) => {
        const productCopy = {...product};
        productCopy.count = productsObj[product._id];
        delete productCopy.description;
        return productCopy;
    });
    setShow(true);
    setOrderToShow(productsToShow);
}

  useEffect(() => {
    setLoading(true);
    axios
      .get("/orders")
      .then(({ data }) => {
        setLoading(false);
        setOrders(data);
      })
      .catch((e) => {
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <Loading />;
  }

  if (orders.length === 0) {
    return <h1 className="text-center pt-4">No Orders Yet!</h1>;
  }

  return (
    <Table responsive striped bordered hover>
      <thead>
        <tr>
          <th>#</th>
          <th>Client Name</th>
          <th>Items</th>
          <th>Order Total</th>
          <th>Address</th>
        </tr>
      </thead>
      <tbody>
        {orders.map((order) => (
          <tr>
            <td>{order._id}</td>
            <td>{order.owner?.name}</td>
            <td>{order.count}</td>
            <td>${order.total}</td>
            <td>{order.address}</td>
            <td>
              {order.status === "Proccessing" ? (
                <Button
                  size="sm"
                  onClick={() => markShipped(order._id, order.owner?._id)}
                >
                  Mark as shipped
                </Button>
              ) : (
                <Badge bg="success">Shipped</Badge>
              )}
            </td>
            <td>
                <span style={{cursor: "pointer"}} onClick={() => showOrder(products)}>
                    View order <i className="fa fa-eye"></i>
                </span>
            </td>
          </tr>
        ))}
      </tbody>
    </Table>
  );
}

export default OrdersAdminPage;
