import React, {useRef, useState} from "react";
import { Navbar, Button, Nav, Container, NavDropdown } from "react-bootstrap";
import { LinkContainer } from "react-router-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { logout } from "../features/userSlice";
import "./Navigation.css";
import axios from "../axios";

const Navigation = () => {
  const user = useSelector((state) => state.user);
  const dispatch = useDispatch();
  const bellRef = useRef(null);
  const notificationRef = useRef(null);
  const [bellPos, setBellPos] = useState({});

  function handleLogout() {
    dispatch(logout());
  }

  const unreadNotifications = user?.notifications?.reduce((acc, current) => {
    if(current.status == "unread") return a + 1;
    return acc;
  }, 0);

  function handleToggleNotifications() {
    const position = bellRef.current.getBoundingClientReact();
    setBellPos(position);
    notificationRef.current.style.display = notificationRef.current.style === "block" ? "none" : "block";
    dispatch(resetNotifications());

    if(unreadNotifications > 1) axios.post(`/user/${user._id}/updateNotifications`);
  };

  return (
    <Navbar
      expand="lg"
      className="
      bg-body-tertiary 
      bg-light 
      data-bs-theme-light 
      fixed-top 
      "
    >
      <Container>
        <LinkContainer to="/">
          <Navbar.Brand>Ecomerce</Navbar.Brand>
        </LinkContainer>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ms-auto">
            {/* If its not a user */}
            {!user && (
              <LinkContainer to="/login">
                <Nav.Link>Login</Nav.Link>
              </LinkContainer>
            )}

            {user && !user.isAdmin &&(
              <LinkContainer to="/cart">
                <Nav.Link>
                  <i className="fas fa-shopping-cart"></i>
                  {user?.cart.count> 0 && (
                    <span className="badge badge-warning" id="cartcount">
                      {user.cart.count}
                    </span>
                  )}
                </Nav.Link>
              </LinkContainer>
            )}

            {/* If its a user */}
            {user && (
              <>
                <Nav.Link style={{position: "relative"}} onClick={handleToggleNotifications}>
                    <a className="fas fa-bell" ref={bellRef} data-count={unreadNotifications || null}></a>
                </Nav.Link>

              <NavDropdown title={`${user.email}`} id="basic-nav-dropdown">
                {user.isAdmin && (
                  <>
                    <LinkContainer to="/dashboard">
                      <NavDropdown.Item>Dashboard</NavDropdown.Item>
                    </LinkContainer>

                    <LinkContainer to="/new-product">
                      <NavDropdown.Item href="#action/3.1">
                        Create Product
                      </NavDropdown.Item>
                    </LinkContainer>
                  </>

                  )}



                {!user.isAdmin && (
                  <>
                    <LinkContainer to="/cart">
                      <NavDropdown.Item>Cart</NavDropdown.Item>
                    </LinkContainer>

                    <LinkContainer to="/orders">
                      <NavDropdown.Item href="#action/3.1">
                        My Orders
                      </NavDropdown.Item>
                    </LinkContainer>
                  </>
                )}

                <NavDropdown.Divider />
                <Button
                  variant="danger"
                  onClick={handleLogout}
                  className="logout-btn"
                >
                  Logout
                </Button>
              </NavDropdown>

              </>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>


    {/*notification*/}
    <div className="notifications-container" style="{{position: 'absolute'}}">
        {user?.notifications.map((notification) => (
            <p className={`notification-${notification.status}`}>
                {notification.message}
                <br />
                <span>{notification.time}</span>
            </p>
        ))}
    </div>

    </Navbar>
  );
};

export default Navigation;
